mapper.select:
SELECT X.outpatient_id id, X.STATUS, DATE_FORMAT( X.update_time, '%Y-%m-%d %T' ) update_time, DATE_FORMAT( X.create_time, '%Y-%m-%d %T' ) create_time, A.id customer_id, A.NAME customer_name, B.id department_id, B.dname departmentName, C.id doctor_id, C.real_name doctor_name, D.id patient_id, D.NAME patientName, D.sex patientSex, D.phone patientPhone, D.nl age, D.birthday patientBirthday FROM bu_diagnose X LEFT JOIN ms_customer A ON A.id = X.customer_id LEFT JOIN hs_department B ON B.id = X.department_id LEFT JOIN hs_user C ON C.id = X.doctor_id LEFT JOIN bu_patient D ON D.id = X.patient_id
and X.create_time >= #{createStartDate}
and X.create_time < #{createEndDate}
and X.customer_id = #{customerId}
and X.department_id = #{departmentId}
and ( X.doctor_id = #{doctorId} or X.doctor_id is null )
and X.patient_id = #{patientId}
and X.type = #{type}
and X.status = #{status}
and (D.name like concat(concat('%',#{term}),'%') or D.phone like concat(concat('%',#{term}),'%')
order by X.create_time desc

select X.id, X.code, X.status, X.type, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, B.id department_id, B.dname department_name, C.id doctor_id, C.realname doctor_name, D.id patient_id, D.name patient_name, Y.user_id from bu_outpatient X join ps_user_patient Y on X.patient_id = Y.patient_id left join hs_department B on B.id = X.department_id left join hs_user C on C.id = X.doctor_id left join bu_patient D on D.id = X.patient_id
and Y.user_id = #{userId}
and X.patient_id = #{patientId}
and X.type = #{type}
and X.status = #{status}

select * from bu_outpatient X join bu_patient P on P.id = X.patient_id
and P.idcard = #{idCard}
and P.phone = #{phone}
and TO_DAYS(X.time) = TO_DAYS(NOW())
