mapper.select:
select distinct P.* from bu_patient P join bu_outpatient X on X.patient_id = P.id
X.doctor_id = #{doctorId}
and (P.name like concat(concat('%',#{term}),'%') or P.phone like concat(concat('%',#{term}),'%') or P.idcard like concat(concat('%',#{term}),'%'))

select distinct P.id, P.code, P.status, P.name, P.sex, P.birthday, P.phone, P.idcard, MAX(DATE_FORMAT(P.create_time, '%Y-%m-%d %T')) last_time from bu_patient P join bu_treatment X on X.patient_id = P.id
X.therapist_id = #{therapistId}
and X.type = #{type}
and (P.name like concat(concat('%',#{term}),'%') or U.phone like concat(concat('%',#{term}),'%') or U.idcard like concat(concat('%',#{term}),'%'))
group by P.id

select P.*, B.dname department_name from bu_prescription P left join bu_patient A on A.id = P.patient_id left join hs_department B on B.id = P.department_id
and P.patient_id = #{patientId}
and P.type = #{type}
