mapper.select:
SELECT temp.id AS prescriptionItemTaskId, temp.outpatient_id,
temp.entity_id, bp.name, bp.phone, bp.id AS patientId, CASE
WHEN bp.sex = 1 THEN '男' WHEN bp.sex = 0 THEN '女' ELSE '未知' END AS gender, TIMESTAMPDIFF( YEAR, bp.birthday, CURDATE()) AS age, hd.dname, temp.appointment_date, temp.time_slot, bpia.treatment_quantity, bpia.quantity, bd.disease, ts.name AS tsName, bd.create_time AS outpatientTime FROM ( SELECT * FROM bu_prescription_item_task WHERE appointment_status = 1
AND (appointment_date BETWEEN #{waitSignatureRo.startDate} AND #{waitSignatureRo.endDate})
UNION
SELECT * FROM bu_prescription_item_task WHERE appointment_status = 0 GROUP BY prescription_item_id, entity_id ORDER BY appointment_status DESC, time_slot ASC ) temp LEFT JOIN bu_patient bp ON temp.patient_id = bp.id AND bp.is_del = 0 LEFT JOIN hs_department hd ON temp.department_id = hd.id AND hd.is_del = 0 LEFT JOIN bu_prescription_item_appointment bpia ON temp.prescription_item_appointment_id = bpia.id AND bpia.is_del = 0 LEFT JOIN bu_diagnose bd ON temp.diagnose_id = bd.id AND bd.is_del = 0 LEFT JOIN ts_sytech ts ON temp.entity_id = ts.id AND ts.is_del = 0 WHERE temp.is_del = 0 AND temp.signature_status = 0 AND temp.customer_id = #{waitSignatureRo.customerId}
AND (bp.name LIKE CONCAT('%',#{waitSignatureRo.search},'%') OR bp.phone LIKE CONCAT('%',#{waitSignatureRo.search},'%'))
AND temp.departmentId = #{waitSignatureRo.departmentId}
AND temp.appointment_status = #{waitSignatureRo.appointmentStatus}

SELECT bpit.id AS prescriptionItemTaskId, bpit.outpatient_id, bpit.entity_id, bp.name, bp.phone, bp.id AS patientId, CASE
WHEN bp.sex = 1 THEN '男' WHEN bp.sex = 0 THEN '女' ELSE '未知' END AS gender, TIMESTAMPDIFF( YEAR, bp.birthday, CURDATE()) AS age, hd.dname, bpit.cure_status, bpit.signature_time, bpia.treatment_quantity, bpia.quantity, bd.disease, bpi.name AS tsName, bpi.detail, bd.create_time AS outpatientTime FROM bu_prescription_item_task bpit LEFT JOIN bu_patient bp ON bpit.patient_id = bp.id AND bp.is_del = 0 LEFT JOIN hs_department hd ON bpit.department_id = hd.id AND hd.is_del = 0 LEFT JOIN bu_prescription_item_appointment bpia ON bpit.prescription_item_appointment_id = bpia.id AND bpia.is_del = 0 LEFT JOIN bu_diagnose bd ON bpit.diagnose_id = bd.id AND bd.is_del = 0 LEFT JOIN bu_prescription_item bpi ON bpit.prescription_item_id = bpi.id AND bpi.is_del = 0 WHERE bpit.is_del = 0 AND bpit.signature_status = 1 and (bpit.cure_status = 0 or bpit.cure_status = 1) AND bpit.customer_id = #{waitTreatmentRo.customerId}
AND (bp.name LIKE CONCAT('%',#{waitTreatmentRo.search},'%') OR bp.phone LIKE CONCAT('%',#{waitTreatmentRo.search},'%'))
AND bpit.departmentId = #{waitTreatmentRo.departmentId}
ORDER BY bpit.signature_time desc
