mapper.select:
SELECT temp.id AS prescriptionItemTaskId, temp.outpatient_id, temp.entity_id, temp.disease_id, bp.name, bp.phone, bp.id AS patientId, CASE
WHEN bp.sex = 1 THEN '男' WHEN bp.sex = 0 THEN '女' ELSE '未知' END AS gender, TIMESTAMPDIFF( YEAR, bp.birthday, CURDATE()) AS age, hd.dname, temp.appointment_date, temp.time_slot, bpia.treatment_quantity, bpia.quantity, bd.disease, temp.ts_name, bp.create_time AS outpatientTime FROM ( SELECT * FROM bu_prescription_item_task WHERE appointment_status = 1
AND (appointment_date BETWEEN #{waitSignatureRo.startDate} AND #{waitSignatureRo.endDate})
AND customer_id = #{waitSignatureRo.customerId}
UNION
SELECT * FROM bu_prescription_item_task WHERE appointment_status = 0
AND customer_id = #{waitSignatureRo.customerId}
GROUP BY prescription_item_id, entity_id ORDER BY appointment_status DESC, time_slot ASC ) temp LEFT JOIN bu_patient bp ON temp.patient_id = bp.his_id and temp.customer_id = bp.customer_id AND bp.is_del = 0 LEFT JOIN hs_department hd ON temp.department_id = hd.his_id and temp.customer_id = hd.customer_id AND hd.is_del = 0 LEFT JOIN bu_prescription_item_appointment bpia ON temp.prescription_item_appointment_id = bpia.id AND bpia.is_del = 0 LEFT JOIN bu_prescription bpr ON temp.prescription_id= bpr.his_id and temp.customer_id = bpr.customer_id AND bpr.is_del = 0 LEFT JOIN bu_diagnose bd ON temp.outpatient_id = bd.outpatient_id and temp.patient_id = bd.patient_id and temp.customer_id = bd.customer_id WHERE temp.is_del = 0 AND temp.signature_status = 0 AND temp.customer_id = #{waitSignatureRo.customerId}
AND (bp.name LIKE CONCAT('%',#{waitSignatureRo.search},'%') OR bp.phone LIKE CONCAT('%',#{waitSignatureRo.search},'%'))
AND temp.department_id = #{waitSignatureRo.departmentId}
AND temp.appointment_status = #{waitSignatureRo.appointmentStatus}
ORDER BY isnull(temp.appointment_date) ASC, temp.time_slot ASC

SELECT bpit.id AS prescriptionItemTaskId, bpit.outpatient_id, bpit.entity_id, bpit.disease_id, bp.name, bp.phone, bp.id AS patientId, CASE
WHEN bp.sex = 1 THEN '男' WHEN bp.sex = 0 THEN '女' ELSE '未知' END AS gender, TIMESTAMPDIFF( YEAR, bp.birthday, CURDATE()) AS age, hd.dname, bpit.cure_status, bpit.signature_time, bpia.treatment_quantity, bpia.quantity, bd.disease, bpit.ts_name, ss.detail, bp.create_time AS outpatientTime FROM bu_prescription_item_task bpit LEFT JOIN bu_patient bp ON bpit.patient_id = bp.his_id AND bp.is_del = 0 AND bpit.customer_id = bp.customer_id LEFT JOIN hs_department hd ON bpit.department_id = hd.his_id AND hd.is_del = 0 AND bpit.customer_id = hd.customer_id LEFT JOIN bu_prescription_item_appointment bpia ON bpit.prescription_item_appointment_id = bpia.id AND bpia.is_del = 0 LEFT JOIN bu_diagnose bd on bpit.patient_id = bd.patient_id and bpit.outpatient_id = bd.outpatient_id and bpit.customer_id = bd.customer_id LEFT JOIN treatment_mapping tm ON bpit.entity_id = tm.hiscode and bpit.customer_id = tm.customer_id AND tm.is_del = 0 LEFT JOIN sch_sytech ss ON tm.code = ss.sytech_id AND bd.disease_id = ss.disease_id AND bd.syndrome_id = ss.syndrome_id AND ss.is_del = 0 WHERE bpit.is_del = 0 AND bpit.signature_status = 1 and (bpit.cure_status = 0 or bpit.cure_status = 1) AND bpit.customer_id = #{waitTreatmentRo.customerId}
AND (bp.name LIKE CONCAT('%',#{waitTreatmentRo.search},'%') OR bp.phone LIKE CONCAT('%',#{waitTreatmentRo.search},'%'))
AND bpit.department_id = #{waitTreatmentRo.departmentId}
ORDER BY bpit.signature_time desc

SELECT temp.name, temp.phone, temp.disease, temp.syndrome, temp.outpatientTime, temp.outpatient_appointment_id, temp.prescription_item_appointment_id, temp.prescription_item_id, temp.id FROM ( SELECT bp.name, bp.phone, dm.his_name AS disease, bd.syndrome, bo.create_time AS outpatientTime, bpit.outpatient_appointment_id, bpit.prescription_item_appointment_id, bpit.prescription_item_id, bpit.id FROM bu_prescription_item_task bpit LEFT JOIN bu_patient bp ON bpit.patient_id = bp.his_id AND bpit.customer_id = bp.customer_id AND bp.is_del = 0 LEFT JOIN bu_outpatient bo ON bpit.outpatient_id = bo.his_id AND bpit.customer_id = bo.customer_id AND bo.is_del = 0 LEFT JOIN bu_diagnose bd ON bpit.patient_id = bd.patient_id AND bpit.outpatient_id = bd.outpatient_id AND bpit.customer_id = bd.customer_id AND bd.is_del = 0 LEFT JOIN disease_mapping dm ON bpit.disease_id = dm.his_code AND bpit.customer_id = dm.customer_id AND dm.is_del = 0 WHERE bpit.is_del = 0 AND bpit.signature_status = 0 AND bpit.customer_id = #{customerId}
AND (dm.his_name LIKE concat('%',#{term},'%') OR bp.name LIKE concat('%',#{term},'%') OR bp.phone LIKE concat('%',#{term},'%') OR bp.idcard LIKE concat('%',#{term},'%'))
ORDER BY bo.create_time ASC LIMIT 999999999 ) temp GROUP BY temp.outpatient_appointment_id
