mapper.select:
select *, count(*) `count` from ( select X.*, D.content disease_name from syn_syndrome X join dict_disease D on D.id = X.disease_id where
symptoms LIKE CONCAT('%',#{symptom},'%')
) X group by id

select X.*, A.content disease_name, B.name syndrome_name, B.symptoms from ( select * from sch_zhongyao
syndrome_id in
#{id}
) X
left join dict_disease A on A.id = X.disease_id left join syn_syndrome B on B.id = X.syndrome_id

select X.*, A.content disease_name, B.name syndrome_name, B.symptoms from ( select * from sch_chengyao
syndrome_id in
#{id}
) X left join dict_disease A on A.id = X.disease_id left join syn_syndrome B on B.id = X.syndrome_id

select X.*, A.content disease_name, B.name syndrome_name, B.symptoms from ( select * from sch_xieding
syndrome_id in
#{id}
) X left join dict_disease A on A.id = X.disease_id left join syn_syndrome B on B.id = X.syndrome_id

select X.*, A.content disease_name, B.name syndrome_name, B.symptoms, C.name sytech_name from ( select * from sch_sytech
syndrome_id in
#{id}
) X left join dict_disease A on A.id = X.disease_id left join syn_syndrome B on B.id = X.syndrome_id left join ts_sytech C on C.id = X.sytech_id
