mapper.select:
select X.id, X.code, X.tcode, X.status, X.type, X.exception, X.feedback, X.evaluation, X.followup, X.followup_uid, DATE_FORMAT(X.time, '%Y-%m-%d %T') time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, DATE_FORMAT(X.followup_time, '%Y-%m-%d %T') followup_time, A.id customer_id, A.name customer_name, B.id department_id, B.dname department_name, C.id therapist_id, C.realname therapist_name, D.id patient_id, D.code patient_code, D.name patient_name, D.sex patient_sex, D.birthday patient_birthday, D.phone patient_phone, D.idcard patient_idcard, T.id project_id, T.name project_name from bu_treatment X left join ts_sytech T on T.id = X.project_id left join ms_customer A on A.id = X.customer_id left join hs_department B on B.id = X.department_id left join hs_user C on C.id = X.therapist_id left join bu_patient D on D.id = X.patient_id
and X.time >= #{startDate}
and X.time < #{endDate}
and X.customer_id = #{customerId}
and X.department_id = #{departmentId}
and X.therapist_id = #{therapistId}
and X.patient_id = #{patientId}
and X.prescription_id = #{prescriptionId}
and X.project_id = #{projectId}
and X.type = #{type}
and X.status = #{status}
and (D.name like concat(concat('%',#{term}),'%') or D.phone like concat(concat('%',#{term}),'%') or X.tcode like concat(concat('%',#{term}),'%'))
order by time desc

select X.id, X.code, X.tcode, X.status, X.type, X.exception, X.feedback, DATE_FORMAT(X.time, '%Y-%m-%d %T') time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, A.id customer_id, A.name customer_name, B.id department_id, B.dname department_name, C.id therapist_id, C.realname therapist_name, D.id patient_id, D.code patient_code, D.name patient_name, D.sex patient_sex, D.birthday patient_birthday, D.phone patient_phone, D.idcard patient_idcard, T.id project_id, T.name project_name from bu_treatment X left join ts_sytech T on T.id = X.project_id left join ms_customer A on A.id = X.customer_id left join hs_department B on B.id = X.department_id left join hs_user C on C.id = X.therapist_id left join bu_patient D on D.id = X.patient_id
X.status = 1 or X.status = 2
and X.time >= #{startDate}
and X.time < #{endDate}
and X.customer_id = #{customerId}
and X.department_id = #{departmentId}
and X.therapist_id = #{therapistId}
order by time desc

select X.id, X.code, X.tcode, X.status, X.type, DATE_FORMAT(X.time, '%Y-%m-%d %T') time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, A.id customer_id, A.name customer_name, B.id department_id, B.dname department_name, C.id therapist_id, C.realname therapist_name, D.id patient_id, D.name patient_name, D.phone patient_phone, T.id project_id, T.name project_name, Y.user_id from bu_treatment X join ps_user_patient Y on X.patient_id = Y.patient_id left join ms_customer A on A.id = X.customer_id left join hs_department B on B.id = X.department_id left join hs_user C on C.id = X.therapist_id left join bu_patient D on D.id = X.patient_id left join ts_sytech T on T.id = X.project_id
and Y.user_id = #{userId}
and X.patient_id = #{patientId}
and X.type = #{type}
and X.status = #{status}
order by time desc

select
    A.id customer_id, A.name customer_name,
    B.id department_id, B.dname department_name,
    C.id therapist_id, C.realname therapist_name,
    count(*) `count`,


            count(case D.id when #{item} then 1 end) count_${item}


from
    bu_treatment X
    left join ms_customer A on A.id = X.customer_id
    left join hs_department B on B.id = X.department_id
    left join hs_user C on C.id = X.therapist_id
    left join ts_sytech D on D.id = X.project_id


        and X.time >= #{startDate}


        and X.time < #{endDate}


        and X.customer_id = #{customerId}


        and X.department_id = #{departmentId}


        and X.therapist_id = #{therapistId}


group by customer_id, therapist_id
