mapper.select:
select X.id, X.type, X.status, X.code, X.identity, X.warranty, DATE_FORMAT(DATE_ADD(X.buy_time, INTERVAL X.warranty MONTH), '%Y-%m-%d %T') warranty_deadline, DATE_FORMAT(X.buy_time, '%Y-%m-%d %T') buy_time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, A.id as agencyId, A.name as agencyName, C.id as customerId, C.name as customerName, M.id as modelId, M.name as modelName, D.content as typeName from te_equip X left join ms_agency A on X.agency_id = A.id left join ms_customer C on X.customer_id = C.id left join te_model M on X.model_id = M.id left join ( select * from dict_common where cate = "CATE_EQUIP_TYPE" ) D on D.value = X.type
and X.agency_id = #{agencyId}
and X.customer_id = #{customerId}
and X.model_id = #{modelId}
and X.type = #{type}
and X.status = #{status}
and (X.code like concat(concat('%',#{term}),'%'))

select X.id, X.type, X.status, X.state, X.code, X.identity, C.id as customerId, C.name as customerName, M.id as modelId, M.name as modelName, N.* from te_equip X left join ms_agency A on X.agency_id = A.id left join ms_customer C on X.customer_id = C.id left join te_model M on X.model_id = M.id left join ( select id rid, code, runid, duration, neck_temp, waist_temp, DATE_FORMAT(start_time, '%Y-%m-%d %T') start_time, DATE_FORMAT(end_time, '%Y-%m-%d %T') end_time, DATE_FORMAT(max(start_time), '%Y-%m-%d %T') last_run_time, TIMESTAMPDIFF(MINUTE, start_time, NOW()) run_minute from tx_xzc_run group by code ) N on X.code = N.code
and X.customer_id = #{customerId}
and X.model_id = #{modelId}
and X.type = #{type}
and X.status = #{status}
and (X.code like concat(concat('%',#{term}),'%'))

select X.id, X.type, X.status, X.code, X.identity, X.warranty, DATE_FORMAT(X.buy_time, '%Y-%m-%d %T') buy_time, DATE_FORMAT(X.update_time, '%Y-%m-%d %T') update_time, DATE_FORMAT(X.create_time, '%Y-%m-%d %T') create_time, A.id as agencyId, A.name as agencyName, C.id as customerId, C.name as customerName, M.id as modelId, M.name as modelName, N.last_maintain_time, D.content as typeName from te_equip X left join ms_agency A on X.agency_id = A.id left join ms_customer C on X.customer_id = C.id left join te_model M on X.model_id = M.id left join ( select eid, max(time) last_maintain_time from te_maintain group by eid ) N on X.id = N.eid left join ( select * from dict_common where cate = "CATE_MAINTAIN_TYPE" ) D on D.value = X.type
and X.agency_id = #{agencyId}
and X.customer_id = #{customerId}
and X.model_id = #{modelId}
and X.type = #{type}
and X.status = #{status}
and (X.code like concat(concat('%',#{term}),'%'))
